commits.content:
.heading {
    margin-top: 100px;
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: #01a2a6;
    padding: 10px 100px;
    color: #fff;
}

/* section {
    padding: 20px 100px;
} */

#page{
    padding: 0 11%;
}

.heading h1 {
    font-size: 40px;
    font-weight: 400;
    color: #fff;
}

.heading p{
    color: #fff;
}

.heading p a{
    color: #ffffffcc;
}

.heading p a:hover{
    color: #fff;
}

.news{
    display: flex;
    justify-content: space-between;
    background-color: #fff;
    padding-top: 70px;
}

.col-left{
    width: 30%;
    padding-right: 15px;
}

.col-right{
    width: 70%;
    padding-left: 15px;
}

.news-search{
    display: flex;
    justify-content: start;
    align-items: center;
}

.news-search form{
    padding: 10px 0;
    position: relative;
}

.news-search form input{
    width: 100%;
    height: 40px;
    border: 1px solid #ccc;
    border-radius: 5px;
    padding: 0 10px;
    font-size: 14px;
}

.news-search form button{
    position: absolute;
    right: 0;
    top: 7px;
    color: #fff;
    background: transparent;
    border: none;
    padding: 10px;
    font-size: 14px;
    cursor: pointer;
}

.news-search form button i{
    font-size: 24px;
    color: #000;
}

.news-categories{
    display: flex;
    flex-direction: column;
    gap: 10px;
}

.news-categories a{
    color: #01a2a6;
    font-size: 16px;
    font-weight: 400;
    padding-left: 5px;
    text-decoration: none;
}

.news-categories a:hover{
    color: #000;
}

.news-body{
    display: flex;
    flex-direction: column;
    gap: 30px;
}

.news-item{
    display: flex;
    justify-content: space-between;
    align-items: center;
    padding: 10px 0;
    border-bottom: 1px solid #ccc;
}

.news-item-content{
    padding-bottom: 10px;
}

.news-item-img{
    width: 100%;
}

.news-item-img img{
    width: 100%;
    height: 100%;
    object-fit: cover;
}

.news-item-info{
    margin-top: 10px;
}

.news-item-title{
    margin-bottom: 5px;
}

.news-item-title a h2{
    font-size: 28px;
    font-weight: 500;
    color: #01a2a6;
}

.news-item-title a:hover h2{
    color: #000;
}

.news-item-description p{
    font-size: 14px;
    color: #666;
    margin-bottom: 10px;
}

.news-item-date p{
    font-size: 14px;
    color: #000;
}

.news-more{
    display: flex;
    justify-content: center;
    align-items: center;
    margin: 30px auto;
    padding: 10px 0;
    border: #ccc 1px solid;
    border-radius: 30px;
    width: 160px;
}

.news-more a{
    color: #01a2a6;
    font-size: 16px;
    font-weight: 400;
    text-decoration: none;
}

.news-more:hover{
    border: #000 1px solid;
}

.news-more:hover a{
    color: #000;
}
